import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import axios from "axios";

const Home = () => {
  const [db, setDb] = useState([]);
  const [con, setCon] = useState("");
  const [mon, setMon] = useState("");
  const [fe, setFe] = useState("");
  const [ti, setTi] = useState("");

  useEffect(() => {
    axios
      .get("http://localhost:7000")
      .then((res) => {
        setDb(res.data);
        // console.log(res.data);
      })
      .catch((error) => alert(error));
  }, []);

  //   useEffect(() => {

  //   }))

  const handleSubmit = () => {
    axios({
      method: "post",
      url: "http://localhost:7000/add",
      data: {
        Concepto: con,
        Monto: mon,
        Fecha: fe,
        Tipo: ti,
      },
    })
      .then((res) => {
        console.log(res);
      })
      .catch((error) => alert(error));
  };

  const handleDelete = (id) => {
      console.log(id);
    axios({
      method: "delete",
      url: "http://localhost:7000/delete/{",
      data: {
        id: id
      },
    })
      .then((res) => {
        console.log(res);
      })
      .catch((error) => alert(error));
  };


  return (
    <div className="container">
      <div className="box">
        <Form onSubmit={handleSubmit}>
          {db.map((data) => (
            <Form.Group>
              <Form.Control type="text" value={data.Concepto} />
              <Form.Control type="text" value={data.Monto + "$"} />
              <Form.Control type="text" value={data.Fecha} />
              <Form.Control type="text" value={data.Tipo} />
              <Button onClick={(e) => handleDelete(data.Id)}>Delete</Button>
            </Form.Group>
          ))}
          <Form.Group>
            <Form.Control
              type="text"
              placeholder="Concepto"
              value={con}
              onChange={(e) => setCon(e.target.value)}
            />
            <Form.Control
              type="text"
              placeholder="Monto"
              value={mon}
              onChange={(e) => setMon(e.target.value)}
            />
            <Form.Control
              type="text"
              placeholder="Fecha"
              value={fe}
              onChange={(e) => setFe(e.target.value)}
            />
            <Form.Control
              type="text"
              placeholder="Tipo"
              value={ti}
              onChange={(e) => setTi(e.target.value)}
            />
            {/* <Form.Control type="text" value={data.Monto + "$"} />
              <Form.Control type="text" value={data.Fecha} />
              <Form.Control type="text" value={data.Tipo} /> */}
          </Form.Group>
          <Button variant="primary" type="submit">
            Submit
          </Button>
        </Form>
      </div>
    </div>
  );
};

export default Home;
