import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import {
  Form,
  FormFeedback,
  FormGroup,
  FormText,
  Label,
  Input,
  Button,
} from "reactstrap";
import axios from "axios";

const Register = () => {
  const [users, setUsers] = useState([]);
  const [user, setUser] = useState({
    username: "",
    password: "",
    comfirmedPassword: "",
  });

  useEffect(() => {
    axios
      .get("http://localhost:7000/users")
      .then((res) => {
        setUsers(res.data);
      })
      .catch((error) => alert(error));
  }, []);

  const handleChange = (evt) => {
    const name = evt.target.name;
    const value =
      evt.target.type === "checkbox" ? evt.target.checked : evt.target.value;
    setUser({
      ...user,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    users.map((data) => {
      if (user.username === data.username) {
        alert('Usuario ya existente')
      } else {
          if(user.password === user.comfirmedPassword) {
            axios({
                method: "post",
                url: "http://localhost:7000/abm/add",
                data: data,
              })
                .then((res) => {
                  console.log(res);
                })
          }
      }
    });
  };

  return (
    <div className="loginContainer">
      <h2>Sign In</h2>
      <Form className="form">
        <FormGroup>
          <Label>Username</Label>
          <Input
            type="username"
            name="username"
            id="exampleUsername"
            placeholder="username"
            value={user.username}
            onChange={(e) => {
              handleChange(e);
            }}
          />
          <FormFeedback>
            Uh oh! Looks like there is an issue with your email. Please input a
            correct email.
          </FormFeedback>
          <FormFeedback valid>
            That's a tasty looking email you've got there.
          </FormFeedback>
          <FormText>Your username is most likely your email.</FormText>
        </FormGroup>
        <FormGroup>
          <Label for="examplePassword">Password</Label>
          <Input
            type="password"
            name="password"
            id="examplePassword"
            placeholder="********"
            value={user.password}
            onChange={(e) => handleChange(e)}
          />
        </FormGroup>
        <FormGroup>
          <Label for="examplePassword">Comfirm Password</Label>
          <Input
            type="password"
            name="comfirmedPassword"
            id="examplePassword"
            placeholder="********"
            value={user.comfirmedPassword}
            onChange={(e) => handleChange(e)}
          />
        </FormGroup>
        <Button color="primary" onClick={handleSubmit}>Submit</Button>
      </Form>
    </div>
  );
};

export default Register;
