import React, { useEffect, useState } from "react";
import {
  Button,
  Modal,
  ModalBody,
  ModalHeader,
  FormGroup,
  ModalFooter,
} from "reactstrap";


const ModalEditar = () => {
  const [modalEditar, setModalEditar] = useState(false);
  const [data, setForm] = useState({
    Concepto: "",
    Monto: "",
    Fecha: "",
    Tipo: "",
  });

  const handleChange = (evt) => {
    const name = evt.target.name;
    const value =
      evt.target.type === "checkbox" ? evt.target.checked : evt.target.value;
    setForm({
      ...data,
      [name]: value,
    });
  };

  const handleUpdate = (id) => {
    axios({
      method: "put",
      url: `http://localhost:7000/update/${id}`,
      data: data,
    })
      .then((res) => {
        console.log(res);
      })
      .then(setModalEditar(false))
      .catch((error) => alert(error));
  };

  const mostrarModalEditar = (data) => {
    setModalEditar(true);
    setForm(data);
  };

  const ocultarModalEditar = () => {
    setModalEditar(false);
  };

  return (
    <div>
      <Modal isOpen={modalEditar}>
        <ModalHeader>
          <div>
            <h3>Editar Registro</h3>
          </div>
        </ModalHeader>
        <ModalBody>
          <FormGroup>
            <label>Id:</label>
            <input
              type="text"
              className="form-control"
              value={data.Id}
              readOnly
            />
          </FormGroup>
          <FormGroup>
            <label>Concepto:</label>
            <input
              type="text"
              className="form-control"
              name="Concepto"
              value={data.Concepto}
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <label>Monto:</label>
            <input
              type="text"
              className="form-control"
              value={data.Monto}
              name="Monto"
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <label>Fecha:</label>
            <input
              type="text"
              className="form-control"
              value={data.Fecha}
              name="Fecha"
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <label>Tipo:</label>
            <input
              type="text"
              className="form-control"
              value={data.Tipo}
              name="Tipo"
              readOnly
            />
          </FormGroup>
        </ModalBody>
        <ModalFooter>
          <Button color="primary" onClick={(e) => handleUpdate(data.Id)}>
            Editar
          </Button>
          <Button color="danger" onClick={ocultarModalEditar}>
            Cancelar
          </Button>
        </ModalFooter>
      </Modal>
    </div>
  );
};

export default ModalEditar;
