import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import moment from "moment";
import {
  Form,
  Table,
  Button,
  Container,
  Modal,
  ModalBody,
  ModalHeader,
  FormGroup,
  ModalFooter,
} from "reactstrap";
import axios from "axios";
import ModalEditar from './components/ModalEditar'
import ModalIncertar from "./components/ModalIncertar";

const Home = () => {
  const [db, setDb] = useState([]);
  const [update, setUpdate] = useState(false);


  let total = 0;
  db.map((ele) => (total += ele.Monto));


  useEffect(
    (db) => {
      axios
        .get("http://localhost:7000/10")
        .then((res) => {
          setDb(res.data);
        })
        // .then(checkIfEmpty)
        .catch((error) => alert(error));
    },
    [modalIncertar, modalEditar, update]
  );

  const handleIncert = () => {
    console.log(data);
    axios({
      method: "post",
      url: "http://localhost:7000/add",
      data: data,
    })
      .then((res) => {
        console.log(res);
      })
      .then(setModalincertar(false))
      .catch((error) => alert(error));
  };

  const handleDelete = (id) => {
    console.log(id);
    axios({
      method: "delete",
      url: `http://localhost:7000/delete/${id}`,
    })
      .then((res) => {
        setUpdate(true);
        console.log(res);
      })
      .then(setUpdate(false))
      .catch((error) => alert(error));
  };


  return (
    <>
      <Container>
        <div className="button_container col">
          <Button
            color="success"
            onClick={mostrarModalIncertar}
            className="incertar"
          >
            New transaction
          </Button>
          <div className="total">Balance: {total} $</div>
        </div>
        <Form>
          <Table>
            <thead>
              <tr>
                <th>Cocepto</th>
                <th>Monto</th>
                <th>Fecha</th>
                <th>Tipo</th>
                <th>Acciones</th>
              </tr>
            </thead>
            <tbody>
              {db.map((data) => (
              <tr key={data.Id}>
                <td>{data.Concepto}</td>
                <td>{data.Monto + "$"}</td>
                <td>{moment(data.Fecha).format("YYYY-MM-DD")}</td>
                <td>{data.Tipo}</td>
                <td>
                  <Button
                    color="primary"
                    onClick={(e) => mostrarModalEditar(data)}
                  >
                    Edit
                  </Button>
                  {"  "}
                  <Button color="danger" onClick={(e) => handleDelete(data.Id)}>
                    Delete
                  </Button>
                </td>
              </tr>
              )) }
            </tbody>
          </Table>
        </Form>
      </Container>
      <ModalIncertar />
      <ModalEditar  />
    </>
  );
};

export default Home;
