import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import {
  Table,
  Button,
  Container,
  Modal,
  ModalBody,
  ModalHeader,
  FormGroup,
  ModalFooter,
} from "reactstrap";
import axios from "axios";

const Home = () => {
  const [db, setDb] = useState();
  const [modalIncertar, setModalincertar] = useState(false);
  const [modalEditar, setModalEditar] = useState(false);
  const [data, setForm] = useState({
    Concepto: "",
    Monto: "",
    Fecha: "",
    Tipo: "",
  });

  useEffect(() => {
    axios
      .get("http://localhost:7000")
      .then((res) => {
        setDb(res.data);
        // console.log(res);
      })
      .catch((error) => alert(error));
  });

  const handleSubmit = () => {
    console.log(data);
    axios({
      method: "post",
      url: "http://localhost:7000/add",
      data: data,
    })
      .then((res) => {
        console.log(res);
      })
      .then(setModalincertar(false))
      .catch((error) => alert(error));
  };

  const handleDelete = (id) => {
    console.log(id);
    axios({
      method: "delete",
      url: `http://localhost:7000/delete/${id}`,
    })
      .then((res) => {
        console.log(res);
      })
      .catch((error) => alert(error));
  };

  const handleChange = (evt) => {
    const name = evt.target.name;
    const value =
      evt.target.type === "checkbox" ? evt.target.checked : evt.target.value;
    setForm({
      ...data,
      [name]: value,
    });
  };

  const mostrarModalIncertar = () => {
    setModalincertar(true);
  };

  const ocultarModalIncertar = () => {
    setModalincertar(false);
  };

  const mostrarModalEditar = () => {
    setModalEditar(true);
  };

  const ocultarModalEditar = () => {
    setModalEditar(false);
  };

  return (
    <>
      <Container>
        <br />
        <Button color="success" onClick={mostrarModalIncertar}>
          New transaction
        </Button>
        <br />
        <br />
        <Table>
          <thead>
            <tr>
              <th>Id</th>
              <th>Cocepto</th>
              <th>Monto</th>
              <th>Fecha</th>
              <th>Tipo</th>
            </tr>
          </thead>
          <tbody>
            {db.map((data) => (
              <tr>
                <td>{data.Concepto}</td>
                <td>{data.Monto + "$"}</td>
                <td>{data.Fecha}</td>
                <td>{data.Tipo}</td>
                <td>
                  <Button
                    color="primary"
                    onClick={(e) => log}
                  >
                    Edit
                  </Button>
                  {"  "}
                  <Button color="danger" onClick={(e) => handleDelete(data.Id)}>
                    Delete
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      </Container>
      <Modal isOpen={modalIncertar}>
        <ModalHeader>
          <div>
            <h3>Incertar Registro</h3>
          </div>
        </ModalHeader>
        <ModalBody>
          <FormGroup>
            <label>Id:</label>
            <input
              type="text"
              className="form-control"
              value={data.Id}
              readOnly
            />
          </FormGroup>
          <FormGroup>
            <label>Concepto:</label>
            <input
              type="text"
              className="form-control"
              name="Concepto"
              value={data.Concepto}
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <label>Monto:</label>
            <input
              type="text"
              className="form-control"
              value={data.Monto}
              name="Monto"
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <label>Fecha:</label>
            <input
              type="text"
              className="form-control"
              value={data.Fecha}
              name="Fecha"
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <label>Tipo:</label>
            <input
              type="text"
              className="form-control"
              value={data.Tipo}
              name="Tipo"
              onChange={handleChange}
            />
          </FormGroup>
        </ModalBody>
        <ModalFooter>
          <Button color="primary" onClick={handleSubmit}>
            Insertar
          </Button>
          <Button color="danger" onClick={ocultarModalIncertar}>
            Cancelar
          </Button>
        </ModalFooter>
      </Modal>

      <Modal isOpen={modalEditar}>
        <ModalHeader>
          <div>
            <h3>Editar Registro</h3>
          </div>
        </ModalHeader>
        <ModalBody>
          <FormGroup>
            <label>Id:</label>
            <input
              type="text"
              className="form-control"
              value={data.Id}
              readOnly
            />
          </FormGroup>
          <FormGroup>
            <label>Concepto:</label>
            <input
              type="text"
              className="form-control"
              name="Concepto"
              value={data.Concepto}
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <label>Monto:</label>
            <input
              type="text"
              className="form-control"
              value={data.Monto}
              name="Monto"
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <label>Fecha:</label>
            <input
              type="text"
              className="form-control"
              value={data.Fecha}
              name="Fecha"
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <label>Tipo:</label>
            <input
              type="text"
              className="form-control"
              value={data.Tipo}
              name="Tipo"
              onChange={handleChange}
            />
          </FormGroup>
        </ModalBody>
        <ModalFooter>
          <Button color="primary" onClick={handleSubmit}>
            Editar
          </Button>
          <Button color="danger" onClick={ocultarModalEditar}>
            Cancelar
          </Button>
        </ModalFooter>
      </Modal>
    </>
  );
};

export default Home;
