import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import {
  Table,
  Button,
  Container,
  Modal,
  ModalBody,
  ModalHeader,
  FormGroup,
  ModalFooter,
} from "reactstrap";
import axios from "axios";

const Home = () => {
  const [db, setDb] = useState([]);
  // const [con, setCon] = useState("");
  // const [mon, setMon] = useState("");
  // const [fe, setFe] = useState("");
  // const [ti, setTi] = useState("");

  useEffect(() => {
    axios
      .get("http://localhost:7000")
      .then((res) => {
        setDb(res.data);
        // console.log(res.data);
      })
      .catch((error) => alert(error));
  });

  // const handleSubmit = () => {
  //   axios({
  //     method: "post",
  //     url: "http://localhost:7000/add",
  //     data: {
  //       Concepto: con,
  //       Monto: mon,
  //       Fecha: fe,
  //       Tipo: ti,
  //     },
  //   })
  //     .then((res) => {
  //       console.log(res);
  //     })
  //     .catch((error) => alert(error));
  // };

  const handleDelete = (id) => {
    console.log(id);
    axios({
      method: "delete",
      url: `http://localhost:7000/delete/${id}`,
    })
      .then((res) => {
        console.log(res);
      })
      .catch((error) => alert(error));
  };

  return (
    <Container>
      <Button color="primary">New transaction</Button>
      <thead><tr><th>Id</th>
        <th>Cocepto</th>
        <th>Monto</th>
        <th>Fecha</th>
        <th>Tipo</th>
        </tr>
      </thead>
      {db.map((data) => (
        <tr>
          <td>{data.Concepto} </td>
          <td>{data.Monto + "$"} </td>
          <td>{data.Fecha} </td>
          <td>{data.Tipo} </td>
          <Button color="danger" onClick={(e) => handleDelete(data.Id)}>
            Delete
          </Button>
        </tr>
      ))}
    </Container>
  );
};

export default Home;

