          <div>
            <h3>Incertar Registro</h3>
          </div>
        </ModalHeader>
        <ModalBody>
          <FormGroup>
            <label>Id:</label>
            <input
              type="text"
              className="form-control"
              value={data.Id}
              readOnly
            />
          </FormGroup>
          <FormGroup>
            <label>Concepto:</label>
            <input
              type="text"
              className="form-control"
              name="Concepto"
              value={data.Concepto}
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <label>Monto:</label>
            <input
              type="text"
              className="form-control"
              value={data.Monto}
              name="Monto"
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <label>Fecha:</label>
            <input
              type="text"
              className="form-control"
              value={data.Fecha}
              name="Fecha"
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <label>Tipo:</label>
            <Input
              type="select"
              className="form-control"
              value={data.Tipo}
              name="Tipo"
              onChange={handleChange}
            >
              <option name="Tipo" value="" hidden></option>
              <option name="Tipo" value="Entrada">
                Entrada
              </option>
              <option name="Tipo" value="Salida">
                Salida
              </option>
            </Input>
          </FormGroup>
        </ModalBody>
        <ModalFooter>
          <Button color="primary" onClick={handleIncert}>
            Insertar
          </Button>
          <Button color="danger" onClick={ocultarModalIncertar}>
            Cancelar
          </Button>
        </ModalFooter>
      </Modal>

      <Modal isOpen={modalEditar}>
        <ModalHeader>
          <div>
            <h3>Editar Registro</h3>
          </div>
        </ModalHeader>
        <ModalBody>
          <FormGroup>
            <label>Id:</label>
            <input
              type="text"
              className="form-control"
              value={data.Id}
              readOnly
            />
          </FormGroup>
          <FormGroup>
            <label>Concepto:</label>
            <input
              type="text"
              className="form-control"
              name="Concepto"
              value={data.Concepto}
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <label>Monto:</label>
            <input
              type="text"
              className="form-control"
              value={data.Monto}
              name="Monto"
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <label>Fecha:</label>
            <input
              type="text"
              className="form-control"
              value={data.Fecha}
              name="Fecha"
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <label>Tipo:</label>
            <input
              type="text"
              className="form-control"
              value={data.Tipo}
              name="Tipo"
              readOnly
            />
          </FormGroup>
        </ModalBody>
        <ModalFooter>
          <Button color="primary" onClick={(e) => handleUpdate(data.Id)}>
            Editar
          </Button>
          <Button color="danger" onClick={ocultarModalEditar}>
            Cancelar
          </Button>
        </ModalFooter>
      </Modal>
    </>
  );
};

export default Home;
