import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import {
  Table,
  Button,
  Container,
  Modal,
  ModalBody,
  ModalHeader,
  FormGroup,
  ModalFooter,
} from "reactstrap";
import axios from "axios";

const db = [
  { Id: 1, Concepto: "Cosas", Monto: "300", Fecha: 3, Tipo: "Compra" },
  { Id: 3, Concepto: "Papa", Monto: "200", Fecha: 2, Tipo: "compra" },
  { Id: 4, Concepto: "", Monto: "100", Fecha: 3, Tipo: "" },
];

const Home = () => {
  // const [db, setDb] = useState();
  const [data, setData] = useState({
    data: db,
    form: {
      id: "",
      concepto: "",
      monto: "",
      fecha: "",
      tipo: "",
    },
  });
  const [modalIncertar , setModalincertar] = useState(false)

  console.log(data.data);
  // console.log(db);
  // const [con, setCon] = useState("");
  // const [mon, setMon] = useState("");
  // const [fe, setFe] = useState("");
  // const [ti, setTi] = useState("");

  // useEffect(() => {
  //   axios
  //     .get("http://localhost:7000")
  //     .then((res) => {
  //       setDb(res.data);
  //       // console.log(res.data);
  //     })
  //     .catch((error) => alert(error));
  // }, []);

  // const handleSubmit = () => {
  //   axios({
  //     method: "post",
  //     url: "http://localhost:7000/add",
  //     data: {
  //       Concepto: con,
  //       Monto: mon,
  //       Fecha: fe,
  //       Tipo: ti,
  //     },
  //   })
  //     .then((res) => {
  //       console.log(res);
  //     })
  //     .catch((error) => alert(error));
  // };

  const handleDelete = (id) => {
    console.log(id);
    axios({
      method: "delete",
      url: `http://localhost:7000/delete/${id}`,
    })
      .then((res) => {
        console.log(res);
      })
      .catch((error) => alert(error));
  };

  const handleChange = (e) => {
    setData({
      form: {
        id: "",
        concepto: "",
        monto: "",
        fecha: "",
        tipo: "",
      },
    });
  };

  const mostrarModalIncertar = () => {
    setModalincertar(true)
  }

  ocultarModalIncertar = () => {
    setModalincertar(false)
  }

  return (
    <>
      <Container>
        <Button color="success">New transaction</Button>
        <thead>
          <tr>
            <th>Id</th>
            <th>Cocepto</th>
            <th>Monto</th>
            <th>Fecha</th>
            <th>Tipo</th>
          </tr>
        </thead>
        {db.map((data) => (
          <tr>
            <td>{data.Concepto} </td>
            <td>{data.Monto + "$"} </td>
            <td>{data.Fecha} </td>
            <td>{data.Tipo} </td>
            <td>
              <Button color="primary" onClick={(e) => handleDelete(data.Id)}>
                Edit
              </Button>
              <Button color="danger" onClick={(e) => handleDelete(data.Id)}>
                Delete
              </Button>
            </td>
          </tr>
        ))}
      </Container>
      <Modal isOpen={setModalincertar} >
        <ModalHeader>
          <div>
            <h3>Incertar Registro</h3>
          </div>
        </ModalHeader>
        <ModalBody>
          <FormGroup>
            <label>Id:</label>
            <input type="text" className="form-control" readOnly />
          </FormGroup>
          <FormGroup>
            <label>Concepto:</label>
            <input
              type="text"
              className="form-control"
              name="concepto"
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <label>Monto:</label>
            <input
              type="text"
              className="form-control"
              name="monto"
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <label>Fecha:</label>
            <input
              type="text"
              className="form-control"
              name="fecha"
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <label>Tipo:</label>
            <input
              type="text"
              className="form-control"
              name="tipo"
              onChange={handleChange}
            />
          </FormGroup>
        </ModalBody>
        <ModalFooter>
          <Button color="primary">Insertar</Button>
          <Button color="danger">Cancelar</Button>
        </ModalFooter>
      </Modal>
    </>
  );
};

export default Home;

