  //       Fecha: fe,
  //       Tipo: ti,
  //     },
  //   })
  //     .then((res) => {
  //       console.log(res);
  //     })
  //     .catch((error) => alert(error));
  // };

  const handleDelete = (id) => {
    console.log(id);
    axios({
      method: "delete",
      url: `http://localhost:7000/delete/${id}`,
    })
      .then((res) => {
        console.log(res);
      })
      .catch((error) => alert(error));
  };

  return (
    <>
    <Container>
      <Button color="success">New transaction</Button>
      <thead>
        <tr>
          <th>Id</th>
          <th>Cocepto</th>
          <th>Monto</th>
          <th>Fecha</th>
          <th>Tipo</th>
        </tr>
      </thead>
      {db.map((data) => (
        <tr>
          <td>{data.Concepto} </td>
          <td>{data.Monto + "$"} </td>
          <td>{data.Fecha} </td>
          <td>{data.Tipo} </td>
          <td>
          <Button color="primary" onClick={(e) => handleDelete(data.Id)}>
            Edit
          </Button>
          <Button color="danger" onClick={(e) => handleDelete(data.Id)}>
            Delete
          </Button></td>
        </tr>
      ))}
    </Container>
    <Modal>
        Modal
    </Modal>
    </>
  );
};

