import React, { useEffect, useState, useHistory, useNavigate } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import {
  Form,
  FormFeedback,
  FormGroup,
  FormText,
  Label,
  Input,
  Button,
} from "reactstrap";
import axios from "axios";
import { withRouter } from "react-router";
// import bcryptjs from "bcryptjs";

const Register = () => {
  const [users, setUsers] = useState([]);
  const [user, setUser] = useState({
    username: "",
    password: "",
    comfirmedPassword: "",
  });
  const [resgistered , setRegistered] = useState(false)

  useEffect(() => {
    axios
      .get("http://localhost:7000/users")
      .then((res) => {
        setUsers(res.data);
      })
      .catch((error) => alert(error));
  }, []);

  const handleChange = (evt) => {
    const name = evt.target.name;
    const value =
      evt.target.type === "checkbox" ? evt.target.checked : evt.target.value;
    setUser({
      ...user,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    if (users === "Not result") {
      if (user.password === user.comfirmedPassword) {
        axios({
          method: "post",
          url: "http://localhost:7000/users/add",
          data: user,
        })
          .then((res) => {
            console.log(res.status);
          })
          .catch((error) => console.log(error));
      }
    } else {
      users.map((data) => {
        if (user.username === data.username) {
          alert("Usuario ya existente");
        } else {
          if (user.password === user.comfirmedPassword) {
            axios({
              method: "post",
              url: "http://localhost:7000/users/add",
              data: user,
            })
              .then((res) => {
                if (res.status === 200) {
                    setRegistered(true)
                }
              })
              .catch((error) => console.log(error));
          }
        }
      });
    }
  };

  return (
    <div className="loginContainer">
      <h2>Register</h2>
      <Form className="form" onSubmit={handleSubmit}>
        <FormGroup>
          <Label>Username</Label>
          <Input
            type="username"
            name="username"
            id="exampleUsername"
            placeholder="username"
            required
            value={user.username}
            onChange={(e) => {
              handleChange(e);
            }}
          />
          <FormFeedback>
            Uh oh! Looks like there is an issue with your email. Please input a
            correct email.
          </FormFeedback>
          <FormFeedback valid>
            That's a tasty looking email you've got there.
          </FormFeedback>
          <FormText>Your username is most likely your email.</FormText>
        </FormGroup>
        <FormGroup>
          <Label for="examplePassword">Password</Label>
          <Input
            type="password"
            name="password"
            id="examplePassword"
            placeholder="********"
            required
            value={user.password}
            onChange={(e) => handleChange(e)}
          />
        </FormGroup>
        <FormGroup>
          <Label for="examplePassword">Comfirm Password</Label>
          <Input
            type="password"
            name="comfirmedPassword"
            id="examplePassword"
            placeholder="********"
            required
            value={user.comfirmedPassword}
            onChange={(e) => handleChange(e)}
          />
        </FormGroup>
        <Button color="primary">Submit</Button>
      </Form>
    </div>
  );
};

export default Register;
